function areAnagrams(str1, str2) {
  if (str1.length !== str2.length) {
    return false;
  }
  let charMap1 = {};
  let charMap2 = {};

  for(let i = 0; i < str1.length; i++) {
    if(!charMap1[str1.charAt(i)]) {
      charMap1[str1.charAt(i)] = 1;
    } else { 
      charMap1[str1.charAt(i)]++;
    }

    if(!charMap2[str2.charAt(i)]) {
      charMap2[str2.charAt(i)] = 1;
    } else { 
      charMap2[str2.charAt(i)]++;
    }
  }

  for(let key in charMap1) {
    if (charMap1[key] != charMap2[key]) {
      return false;
    }
  }

  return true;

}

