// structure of buffer
typedef struct {
 int buffer[MAX_BUFFER_SIZE];
 int counter;
} buffer_t;

// initialize buffer
void init_buffer(buffer_t *buffer) {
 buffer->counter = 0;
}

// produce an item and add it to the buffer
void produce(buffer_t *buffer, int item) {
 if (buffer->counter < MAX_BUFFER_SIZE) {
  buffer->buffer[buffer->counter++] = item;
 }
}

// consume an item from the buffer
int consume(buffer_t *buffer) {
 if (buffer->counter > 0) {
  int item = buffer->buffer[--buffer->counter];
