function longestCommonSubstring(str1, str2) {
    let max = 0;
    let curr = 0;
    let start = -1;
    let ans = null;

    let m = str1.length;
    let n = str2.length;

    /* Build an array to search */
    let arr = new Array(m);
    for(let i = 0; i < m; i++) {
      arr[i] = new Array(n);
      arr[i].fill(0);
    }

    /* Traverse the array and check if the two chars match */
    for(let i = 0; i < m; i++) {
      for(let j = 0; j < n; j++) {
        if(str1[i] === str2[j]) {
            arr[i][j] = (i === 0 || j === 0) ? 1 : (arr[i-1][j-1] + 1);

            curr = arr[i][j];
            if(curr > max) {
              max = curr;
              start = i - max + 1;
            }
        }
      }
    }

    return str1.substring(start, start + max);
}

