void reverseSentence(char *str) 
{ 
    reverse(str); 

    int start = 0, end; 
    while (str[start] != '\0') { 
        if (str[start] == ' ') { 
            start++; 
            continue;  
        } 

        end = start + 1; 
        while (str[end] != ' ' && str[end] != '\0') 
            end++; 

        reverse(str + start, end - start); 

		start = end; 
    } 
} 

