// Compare two dates 
int CompareDates::compare()const
{ 
    if (fYear != sYear) 
        return (fYear > sYear)? 1: -1; 

    if (fMonth != sMonth)
        return (fMonth > sMonth)? 1: -1; 

    if (fDate != sDate)
        return (fDate > sDate)? 1: -1; 

    return 0; 
} 

