public ListNode reverseKGroup(ListNode head, int k) {
    ListNode curr = head;
    ListNode prev = null;

    int count = 0;

    while (curr != null && count != k) {
        ListNode next = curr.next;
        curr.next = prev;
        prev = curr;
        curr = next;
        count++;
    }

    if (next != null) {
        head.next = reverseKGroup(next, k);
    }

    return prev;
}

